<?php		$args = array('post_type' => 'vacancies', 'posts_per_page' => -1);	$zerif_query = new WP_Query( $args );	if ( $zerif_query->have_posts() ):						echo '<section class="works" id="vacancies">';			echo '<div class="container">';				echo '<div class="section-header">';									$zerif_portofolio_title = get_theme_mod('zerif_portofolio_title','Vacancies');                    $zerif_portofolio_title_lang = get_theme_mod('zerif_portofolio_title_lang','Вакансії');					if( !empty($zerif_portofolio_title) ):                        if (ICL_LANGUAGE_CODE == 'en'):                            echo '<h2 class="white-text">'.$zerif_portofolio_title.'</h2>';                        elseif (ICL_LANGUAGE_CODE == 'ua'):                            echo '<h2 class="white-text">'.$zerif_portofolio_title_lang.'</h2>';                        endif;											endif;						$zerif_portofolio_subtitle = get_theme_mod('zerif_portofolio_subtitle','We are looking for talented teammates all the time! Explore opportunities with Developex!');                    $zerif_portofolio_subtitle_lang = get_theme_mod('zerif_portofolio_subtitle_lang','Ми шукаємо талановитих товаришів по команді весь час! Дослідіть можливості з Developex!');					if( !empty($zerif_portofolio_subtitle) ):                        if (ICL_LANGUAGE_CODE == 'en'):                            echo '<h6 class="white-text">'.$zerif_portofolio_subtitle.'</h6>';                        elseif (ICL_LANGUAGE_CODE == 'ua'):                            echo '<h6 class="white-tet">'.$zerif_portofolio_subtitle_lang.'</h6>';                        endif;					endif;				echo '</div>';				echo '<div class="row projects">';					echo '<div id="loader">';						echo '<div class="loader-icon"></div>';					echo '</div>';					echo '<div class="col-md-12" id="portfolio-list">';					echo '<ul class="cbp-rfgrid">';					while ( $zerif_query->have_posts() ) :						$zerif_query->the_post();						?>						<!-- PROJECT -->							<li data-scrollreveal="enter left after 0s over 1s">						<a href="<?php the_permalink(); ?>" class="more">							<?php							if ( has_post_thumbnail($post->ID) ):								echo get_the_post_thumbnail($post->ID, 'zerif_project_photo'); 							endif;							?>							<div class="project-info">								<div class="project-details">									<h5 class="white-text red-border-bottom">										<?php the_title(); ?>									</h5>									<div class="details white-text">                                        <?php echo mb_substr( strip_tags( get_the_content() ), 0, 100 ); ?>                                    </div>								</div>							</div>							</a>						</li>						<!-- / PROJECT -->						<?php					endwhile;					echo '</ul>';					echo '</div>';				echo '</div>';			echo '<div id="loaded-content"></div>';			echo '<a id="back-button" class="red-btn" href="#"><i class="icon-fontawesome-webfont-27"></i>'.__('Go Back','zerif').'</a>';		echo '</div>';	echo '</section>';	endif;	wp_reset_postdata();?>